css:
/*
@cyan:         #35a766;
@blue:         #5977d3;
@purple:       #8100c6;
@green:        #8fad06;
@red:          #d24d4d;
@orange:       #cd8e21;
@light-orange: #e3a92d;
*/
atom-text-editor kite-minimap-wrapper,
html kite-minimap-wrapper {
  display: flex;
  overflow: hidden;
  position: relative;
  -webkit-user-select: none;
  height: 100%;
  order: 3;
  width: 10%;
  flex: 0 0 10%;
  flex-direction: column;
}
atom-text-editor kite-minimap-wrapper i.icon-remove-close,
html kite-minimap-wrapper i.icon-remove-close {
  position: absolute;
  top: 10px;
  right: 0px;
  z-index: 2;
}
atom-text-editor kite-minimap-wrapper i.icon-remove-close::before,
html kite-minimap-wrapper i.icon-remove-close::before {
  width: auto;
  font-size: 0.9em;
}
atom-text-editor kite-minimap-wrapper div,
html kite-minimap-wrapper div {
  position: relative;
}
atom-text-editor kite-minimap-wrapper.left,
html kite-minimap-wrapper.left {
  order: 1;
}
atom-text-editor kite-minimap-wrapper.left > div,
html kite-minimap-wrapper.left > div {
  padding-left: 5px;
}
atom-text-editor kite-minimap-wrapper.left > div i.icon-remove-close::before,
html kite-minimap-wrapper.left > div i.icon-remove-close::before {
  margin-right: 0;
}
atom-text-editor kite-minimap-wrapper.absolute,
html kite-minimap-wrapper.absolute {
  position: absolute;
  right: 0;
}
atom-text-editor kite-minimap-wrapper.absolute atom-text-editor-minimap:not([stand-alone]),
html kite-minimap-wrapper.absolute atom-text-editor-minimap:not([stand-alone]) {
  position: relative;
}
atom-text-editor kite-minimap-wrapper.absolute.adjust-absolute-height,
html kite-minimap-wrapper.absolute.adjust-absolute-height {
  pointer-events: none;
}
atom-text-editor kite-minimap-wrapper.absolute.adjust-absolute-height > div,
html kite-minimap-wrapper.absolute.adjust-absolute-height > div {
  pointer-events: auto;
}
atom-text-editor kite-minimap-wrapper.absolute.left,
html kite-minimap-wrapper.absolute.left {
  position: relative;
  right: initial;
  order: 0;
}
atom-text-editor kite-minimap-wrapper atom-text-editor-minimap:not([stand-alone]),
html kite-minimap-wrapper atom-text-editor-minimap:not([stand-alone]) {
  width: 100%;
  flex: 1 1 auto;
  order: 0;
}
atom-text-editor kite-minimap-wrapper .collapser,
html kite-minimap-wrapper .collapser {
  cursor: pointer;
}
atom-text-editor kite-minimap-wrapper .collapser *,
html kite-minimap-wrapper .collapser * {
  pointer-events: none;
}
atom-text-editor kite-minimap-wrapper .collapser i,
html kite-minimap-wrapper .collapser i {
  height: 16px;
  display: inline-block;
  vertical-align: middle;
}
atom-text-editor kite-minimap-wrapper .collapser i::before,
html kite-minimap-wrapper .collapser i::before {
  transition: transform 0.2s;
  margin: 0;
  line-height: 1em;
  text-align: center;
}
atom-text-editor kite-minimap-wrapper .collapser.collapse i::before,
html kite-minimap-wrapper .collapser.collapse i::before {
  transform: rotate(-90deg);
}
atom-text-editor kite-minimap-wrapper .collapser.collapse + ul,
html kite-minimap-wrapper .collapser.collapse + ul {
  display: none;
}
atom-text-editor kite-minimap-wrapper > div,
html kite-minimap-wrapper > div {
  font-size: 1em;
  padding: 10px 0;
  order: 1;
  font-family: sans-serif;
}
atom-text-editor kite-minimap-wrapper > div ul,
html kite-minimap-wrapper > div ul {
  margin: 0;
  padding: 0;
  list-style: none;
}
atom-text-editor kite-minimap-wrapper > div a,
html kite-minimap-wrapper > div a {
  color: #157AFB;
}
