css:
/*
@cyan:         #35a766;
@blue:         #5977d3;
@purple:       #8100c6;
@green:        #8fad06;
@red:          #d24d4d;
@orange:       #cd8e21;
@light-orange: #e3a92d;
*/
#linter-tooltip {
  margin-top: 3px;
}
#linter-tooltip linter-message.error {
  color: #444;
  border-left: 8px solid #FB3A37;
  background-color: rgba(0, 0, 0, 0.1);
}
#linter-tooltip linter-message.error:last-child::before {
  border-color: transparent transparent #FB3A37 #FB3A37;
}
#linter-tooltip linter-message.error a {
  color: #444;
}
#linter-tooltip linter-message.warning {
  color: #444;
  border-left: 8px solid #F7A550;
  background-color: rgba(0, 0, 0, 0.1);
}
#linter-tooltip linter-message.warning:last-child::before {
  border-color: transparent transparent #F7A550 #F7A550;
}
#linter-tooltip linter-message.warning a {
  color: #444;
}
#linter-tooltip linter-message.info {
  color: #444;
  border-left: 8px solid #157AFB;
  background-color: rgba(0, 0, 0, 0.1);
}
#linter-tooltip linter-message.info:last-child::before {
  border-color: transparent transparent #157AFB #157AFB;
}
#linter-tooltip linter-message.info a {
  color: #444;
}
#linter-tooltip linter-message.info::before {
  opacity: 0;
}
#linter-tooltip linter-message:last-child::before {
  content: "";
  position: absolute;
  top: -4px;
  left: 0;
  border: 4px solid;
}
#linter-tooltip linter-message a {
  font-weight: bold;
}
#linter-tooltip linter-message p {
  margin-bottom: 0;
}
#linter-tooltip linter-message .linter-line {
  display: block;
}
#linter-tooltip linter-message .badge {
  color: #000;
}
linter-message {
  display: block;
  padding: 2px 5px;
}
.icon.linter-icon::before {
  font-size: 14px;
}
atom-text-editor.editor .linter-row {
  /* Take up the full allowed width */
  left: 0;
  right: 0;
  /* Align the linter dot in the middle */
  display: flex;
  align-items: center;
  justify-content: center;
}
atom-text-editor.editor .linter-highlight.linter-info .region,
.linter-highlight.linter-info .region {
  background-image: linear-gradient(45deg, transparent 65%, #157AFB 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #157AFB 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #157AFB 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #157AFB 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-info.linter-gutter,
.linter-highlight.linter-info.linter-gutter {
  color: #157AFB;
  z-index: -1;
}
atom-text-editor.editor .linter-highlight.linter-warning .region,
.linter-highlight.linter-warning .region {
  background-image: linear-gradient(45deg, transparent 65%, #F7A550 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #F7A550 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #F7A550 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #F7A550 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-warning.linter-gutter,
.linter-highlight.linter-warning.linter-gutter {
  color: #F7A550;
  z-index: 0;
}
atom-text-editor.editor .linter-highlight.linter-error .region,
.linter-highlight.linter-error .region {
  background-image: linear-gradient(45deg, transparent 65%, #FB3A37 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #FB3A37 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #FB3A37 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #FB3A37 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-error.linter-gutter,
.linter-highlight.linter-error.linter-gutter {
  color: #FB3A37;
  z-index: 1;
}
.tree-view span.name {
  position: relative;
}
.tree-view .linter-info {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #157AFB 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #157AFB 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #157AFB 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #157AFB 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.tree-view .linter-warning {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #F7A550 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #F7A550 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #F7A550 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #F7A550 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.tree-view .linter-error {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #FB3A37 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #FB3A37 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #FB3A37 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #FB3A37 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.linter-gutter.icon::before {
  width: 100%;
  font-size: 1em;
}
.linter-status-count a {
  padding: 2px;
}
.linter-status-count span.icon::before {
  font-size: 12px;
  vertical-align: middle;
}
.linter-status-count.hide-config,
.linter-status-count.hide-pane {
  display: none;
}
.linter-cursor-line {
  width: 100%;
  padding-left: 0;
}
linter-decoration {
  pointer-events: none;
}
