.item-views {
  background: #f8f6f1 !important;
}
[data-typewriter="true"] {
  max-width: 100%;
  margin: 0 auto;
}
[data-typewriter="true"] .gutter {
  display: none;
}
[data-typewriter="true"] .editor--private .vertical-scrollbar {
  opacity: 0;
  transition: opacity .2s .1s;
}
[data-typewriter="true"] .editor--private:hover .vertical-scrollbar {
  opacity: 1;
}
