css:
#linter-tooltip {
  margin-top: 3px;
}
#linter-tooltip linter-message.error {
  color: #212529;
  border-left: 8px solid #fa5252;
  background-color: #ffffff;
}
#linter-tooltip linter-message.error:last-child::before {
  border-color: transparent transparent #fa5252 #fa5252;
}
#linter-tooltip linter-message.error a {
  color: #212529;
}
#linter-tooltip linter-message.warning {
  color: #212529;
  border-left: 8px solid #12b886;
  background-color: #ffffff;
}
#linter-tooltip linter-message.warning:last-child::before {
  border-color: transparent transparent #12b886 #12b886;
}
#linter-tooltip linter-message.warning a {
  color: #212529;
}
#linter-tooltip linter-message.info {
  color: #212529;
  border-left: 8px solid #228ae6;
  background-color: #ffffff;
}
#linter-tooltip linter-message.info:last-child::before {
  border-color: transparent transparent #228ae6 #228ae6;
}
#linter-tooltip linter-message.info a {
  color: #212529;
}
#linter-tooltip linter-message.info::before {
  opacity: 0;
}
#linter-tooltip linter-message:last-child::before {
  content: "";
  position: absolute;
  top: -4px;
  left: 0;
  border: 4px solid;
}
#linter-tooltip linter-message a {
  font-weight: bold;
}
#linter-tooltip linter-message p {
  margin-bottom: 0;
}
#linter-tooltip linter-message .linter-line {
  display: block;
}
#linter-tooltip linter-message .badge {
  color: #212529;
}
linter-message {
  display: block;
  padding: 2px 5px;
}
.icon.linter-icon::before {
  font-size: 14px;
}
atom-text-editor.editor .linter-row {
  /* Take up the full allowed width */
  left: 0;
  right: 0;
  /* Align the linter dot in the middle */
  display: flex;
  align-items: center;
  justify-content: center;
}
atom-text-editor.editor .linter-highlight.linter-info .region,
.linter-highlight.linter-info .region {
  background-image: linear-gradient(45deg, transparent 65%, #228ae6 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #228ae6 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #228ae6 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #228ae6 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-info.linter-gutter,
.linter-highlight.linter-info.linter-gutter {
  color: #228ae6;
  z-index: -1;
}
atom-text-editor.editor .linter-highlight.linter-warning .region,
.linter-highlight.linter-warning .region {
  background-image: linear-gradient(45deg, transparent 65%, #fd7e14 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #fd7e14 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #fd7e14 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #fd7e14 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-warning.linter-gutter,
.linter-highlight.linter-warning.linter-gutter {
  color: #fd7e14;
  z-index: 0;
}
atom-text-editor.editor .linter-highlight.linter-error .region,
.linter-highlight.linter-error .region {
  background-image: linear-gradient(45deg, transparent 65%, #fa5252 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #fa5252 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #fa5252 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #fa5252 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-error.linter-gutter,
.linter-highlight.linter-error.linter-gutter {
  color: #fa5252;
  z-index: 1;
}
.tree-view span.name {
  position: relative;
}
.tree-view .linter-info {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #228ae6 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #228ae6 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #228ae6 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #228ae6 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.tree-view .linter-warning {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #fd7e14 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #fd7e14 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #fd7e14 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #fd7e14 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.tree-view .linter-error {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #fa5252 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #fa5252 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #fa5252 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #fa5252 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.linter-gutter.icon::before {
  width: 100%;
  font-size: 1em;
}
.linter-status-count a {
  padding: 2px;
}
.linter-status-count span.icon::before {
  font-size: 12px;
  vertical-align: middle;
}
.linter-status-count.hide-config,
.linter-status-count.hide-pane {
  display: none;
}
.linter-cursor-line {
  width: 100%;
  padding-left: 0;
}
linter-decoration {
  pointer-events: none;
}
